mapper.select:
SELECT ceo.id as id, ceo.tid as tid, ceo.uid as uid, ceo.uname as uname, ceo.withdraw_order_number as withdrawOrderNumber, ceo.third_party_order_number as thirdPartyOrderNumber, ceo.channel as channel, ceo.withdraw_money as withdrawMoney, ceo.transfer_amount as transferAmount, ceo.poundage as poundage, ceo.withdraw_number as withdrawNumber, ceo.history_withdraw_money as historyWithdrawMoney, ceo.accumulate_water as accumulateWater, ceo.accumulate_recharge as accumulateRecharge, ceo.accumulate_excoins as accumulateExcoins, ceo.recharge_excoins as rechargeExcoins, ceo.activity_rewards as activityRewards, ceo.off_line_excoins as offLineExcoins, ceo.register_ip as registerIp, ceo.withdraw_ip as withdrawIp, ceo.withdraw_type as withdrawType, ceo.transfer_type as transferType, ceo.exaa_status as exaaStatus, ceo.remark as remark, ceo.create_time as createTime, ceo.update_time as updateTime, ceo.update_by as updateBy, ue.account as withdrawAccount, ue.name as withdrawName from config_exchange_order ceo left join user_exchange ue on (ceo.uid = ue.uid and ceo.withdraw_type = ue.type) where 1 = 1 and ceo.tid = #{tid}
and ceo.withdraw_order_number = #{withdrawOrderNumber}
and ceo.third_party_order_number = #{thirdPartyOrderNumber}
and ceo.exaa_status = #{exaaStatus}
and ceo.withdraw_type = #{withdrawType}
and ceo.transfer_type = #{transferType}
and ceo.create_time >= #{createTime1}
and cro.create_time <= #{finishTime2}

mapper.update:
update config_exchange_order
remark = #{remark}, exaa_status = #{exaaStatus}, transfer_type = #{transferType}, update_time = sysdate()
where id = #{id} and tid=#{tid}
