mapper.select:
SELECT shrl.id as id, shrl.tid as tid, shrl.content as content, shrl.interval_title as intervalTitle, shrl.state as state, shrl.horse_race_grade as horseRaceGrade, shrl.begin_time as beginTime, shrl.end_time as endTime, shrl.create_time as createTime, shrl.update_time as updateTime, shrl.create_by as createBy, shrl.update_by as updateBy from sys_horse_race_lamp shrl where tid = #{tid}
and shrl.id = #{id}
and shrl.state = #{state}
and date_format(shrl.create_time,'%Y-%m-%d') >= #{beginTime}
and date_format(shrl.create_time,'%Y-%m-%d') <= #{endTime}

mapper.update:
update sys_horse_race_lamp
content = #{content}, interval_title = #{intervalTitle}, state = #{state}, horse_race_grade = #{horseRaceGrade}, begin_time = #{beginTime}, end_time = #{endTime}, play_begin_time = #{playBeginTime}, play_end_time = #{playEndTime}, update_time = sysdate(), update_by = #{updateBy}
where id = #{id} and tid=#{tid}
