mapper.update:
update config_vip_recharge
status = #{status}, open_type = #{openType}, vip_name = #{vipName}, img_type = #{imgType}, recharge_type = #{rechargeType}, recharge_other_type = #{rechargeOtherType},
ys_id = #{ysid}, live_addr = #{liveAddr}, url = #{url}, qq = #{qq}, wechat = #{wechat}, update_time = sysdate(), update_by = #{updateBy}
where id = #{id} and tid=#{tid}

mapper.select:
select id, pay_id payid,pay_type payType,vip_list vipList,other_name otherName,is_diy isDiy,diy_min diyMin,
diy_max diyMax,btn,pay_url payUrl,sort,channel_list channelList, use_mobile useMobile,status,update_time
updateTime,update_by updateBy,msg
from config_online_recharge

    tid = #{tid}
    and pay_type = #{payType}
    and status = #{status}
    and vip_list like concat('%',#{vipList},'%')

mapper.update:
update config_online_recharge
pay_id = #{payid}, pay_type = #{payType}, vip_list = #{vipList}, other_name = #{otherName}, is_diy = #{isDiy}, diy_min = #{diyMin}, diy_max = #{diyMax}, btn = #{btn}, pay_url = #{payUrl}, sort = #{sort}, channel_list = #{channelList}, use_mobile = #{useMobile}, status = #{status}, msg = #{msg}, update_time = sysdate(), update_by = #{updateBy}
where id = #{id} and tid=#{tid}

mapper.select:
select id,vip_list vipList,open_type openType,btn,live_addr liveAddr,bank_value bankValue,update_time
updateTime,update_by updateBy,status
from config_bank_recharge

    tid = #{tid}
    and status = #{status}
    and vip_list like concat('%',#{vipList},'%')

mapper.update:
update config_bank_recharge
vip_list = #{vipList}, open_type = #{openType}, btn = #{btn}, live_addr = #{liveAddr}, bank_value = #{bankValue}, status = #{status}, update_time = sysdate(), update_by = #{updateBy}
where id = #{id} and tid = #{tid}

update config_month_recharge
jin_recharge = #{jinRecharge}, jin_recharge_give = #{jinRechargeGive}, jin_recharge_give_day = #{jinRechargeGiveDay},
yin_recharge = #{yinRecharge}, yin_recharge_give = #{yinRechargeGive}, yin_recharge_give_day = #{yinRechargeGiveDay},
open_type = #{openType}, live_addr = #{liveAddr}, wechat = #{wechat}, qq = #{qq}, update_time = sysdate(), update_by = #{updateBy}
where id = #{id} and tid = #{tid}

update config_ys
username = #{username}, password = #{password}, google_check = #{googleCheck}, google_key = #{googleKey}, google_key = #{googleKey}, recharge_status = #{rechargeStatus}, update_time = sysdate()
where id = #{id} and tid = #{tid}

mapper.select:
select id ysid,username,amount `value`
from config_ys
where id = #{ysid}

select username,value/10000 value ,amount/10000 amount,type,create_time createTime
from config_ys_quota
where ysid = #{ysid}
 and type=#{type}
 and create_time>=#{beginTime}
 and #{endTime}>=create_time

select sum(value) value from(
    select username,value/10000 value ,amount/10000 amount,type,create_time createTime
    from config_ys_quota
    where ysid = #{ysid}
     and type=#{type}
     and create_time>=#{beginTime}
     and #{endTime}>=create_time
)t
