mapper.select:
SELECT t_id tid,t_name tName FROM sys_tenant
WHERE t_type = #{tType}

  and parent_id = #{tid}

select * from sys_tenant
t_id = #{tId}
and t_type = #{tType}
and date_format(create_time,'%Y-%m-%d %H:%i-%s') >= date_format(#{beginTime},'%Y-%m-%d %H:%i-%s')
and date_format(create_time,'%Y-%m-%d %H:%i-%s') <= date_format(#{endTime},'%Y-%m-%d %H:%i-%s')
order by t_id desc

mapper.insert:
insert into sys_tenant (t_id, parent_id, ancestors,
  t_name, status, del_flag, create_by,
  create_time, update_by, update_time,
  t_type, tenant, description
  )
values (#{tId,jdbcType=VARCHAR}, #{parentId,jdbcType=BIGINT}, #{ancestors,jdbcType=VARCHAR},
  #{tName,jdbcType=VARCHAR}, #{status,jdbcType=CHAR}, #{delFlag,jdbcType=CHAR}, #{createBy,jdbcType=VARCHAR},
  #{createTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP},
  #{tType,jdbcType=SMALLINT}, #{tenant,jdbcType=BIGINT}, #{description,jdbcType=VARCHAR}
  )
