mapper.select:
select o.order_number orderNumber, o.uid, r.name, o.before_order_money beforeOrderMoney, o.after_order_money afterOrderMoney, o.ex_coins exCoins, o.recharge_amount rechargeAmount, o.payment_status paymentStatus, o.create_time createTime, o.finish_time finishTime from config_recharge_order o left join data_register r on o.uid = r.uid where o.admin_user_id = #{ysid}
and o.uid = #{uid}
and o.finish_time >= #{beginTime}
and #{endTime} >= o.finish_time

select count(1) nums,sum(rechargeAmount) countAmount from( select o.order_number orderNumber, o.uid, r.name, o.before_order_money beforeOrderMoney, o.after_order_money afterOrderMoney, o.ex_coins exCoins, o.recharge_amount rechargeAmount, o.payment_status paymentStatus, o.create_time createTime, o.finish_time finishTime from config_recharge_order o left join data_register r on o.uid = r.uid where o.admin_user_id = #{ysid}
and o.uid = #{uid}
and o.finish_time >= #{beginTime}
and #{endTime} >= o.finish_time
) t

SELECT DATE_FORMAT(create_time, '%Y-%m-%d') day ,COUNT(1) orders ,SUM(IF(TYPE=1,VALUE,0))/10000 transferIn ,SUM(IF(TYPE=2,VALUE,0))/10000 transferOut ,(SELECT amount FROM config_ys_quota WHERE DATE_FORMAT(create_time, '%Y-%m-%d') = day ORDER BY create_time DESC LIMIT 0,1) amount FROM config_ys_quota WHERE ysid = #{ysid}
and create_time >= #{beginTime}
and #{endTime} >= create_time
GROUP BY DATE_FORMAT(create_time, '%Y-%m-%d')

select id,uid,name,bank,create_time createTime
from user_mark
where tid = #{tid}
and uid = #{uid}
and create_time >= #{beginTime}
and #{endTime} >= create_time

select id,uid,name,bank,mark,status,create_time createTime,update_time updateTime
from user_black
where tid = #{tid}
and uid = #{uid}
and update_time >= #{beginTime}
and #{endTime} >= update_time
